// eslint-disable-next-line
import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Row, Col } from "react-bootstrap";
import Course from "../components/Course";
import Message from "../components/Message";
import Loader from "../components/Loader";
import { listCourses } from "../actions/courseActions";
import { Link, useParams } from "react-router-dom"; // Import useParams to get the keyword from the URL
import Meta from "../components/Meta.js";
import CourseCarousel from "../components/CourseCarousel.js";

const HomeScreen = ({match}) => {
  const { keyword } = useParams(); // Use useParams to get the keyword from the URL
  // const keyword = match.params.keyword;
  // For demonstration, using hardcoded courses array
  //const courses = [{name: 'Algebra'}, {name: 'Calculus'}]; // Example courses


  const dispatch = useDispatch();

  const courseList = useSelector((state) => state.courseList);
  const { loading, error, courses } = courseList;

  //whatever we put inisde the useEffect func will run when the HomeScreen component loads
  //what we want is that our coreses loads to the screen
  useEffect(() => {
    dispatch(listCourses(keyword));
  }, [dispatch , keyword]); // Add keyword to dependency array

  return (
    <>
      <Meta />
      {!keyword ? <CourseCarousel/> : <Link to='/' className='btn btn-light'>Go Back </Link> }
      <h1>Our Courses</h1>
      {loading ? (
        <Loader />
      ) : error ? (
        <Message variant='danger'>{error}</Message>
      ) : (
        <Row>
          {courses.map((course) => (
          <Col key={course.name} sm={12} md={6} lg={4} xl={3}>
            <Link to={`/teachers?course=${course.name}`}>
              <div>{course.name}</div>
            </Link>
          </Col>
        ))}
        </Row>
      )}
    </>
  );
};

export default HomeScreen;
