// AddScheduleScreen.js
import React, { useState } from 'react';
import { Form, Button, Table } from 'react-bootstrap';

const AddScheduleScreen = () => {
  const [date, setDate] = useState('');
  const [time, setTime] = useState('');
  const [schedule, setSchedule] = useState([]);

  const addScheduleHandler = () => {
    // Add the new date and time to the schedule array
    setSchedule([...schedule, { date, time }]);
    setDate('');
    setTime('');
  };

  const submitHandler = (e) => {
    e.preventDefault();
    // POST the schedule to the backend
    // axios.post('/api/schedule', { schedule })...
  };

  return (
    <>
      <Form onSubmit={submitHandler}>
        <Form.Group controlId='date'>
          <Form.Label>Date</Form.Label>
          <Form.Control
            type='date'
            value={date}
            onChange={(e) => setDate(e.target.value)}
          />
        </Form.Group>

        <Form.Group controlId='time'>
          <Form.Label>Time</Form.Label>
          <Form.Control
            type='time'
            value={time}
            onChange={(e) => setTime(e.target.value)}
          />
        </Form.Group>

        <Button variant='primary' onClick={addScheduleHandler}>
          Add to Schedule
        </Button>
      </Form>

      <Table striped bordered hover responsive className='mt-3'>
        <thead>
          <tr>
            <th>Date</th>
            <th>Time</th>
          </tr>
        </thead>
        <tbody>
          {schedule.map((slot, index) => (
            <tr key={index}>
              <td>{slot.date}</td>
              <td>{slot.time}</td>
            </tr>
          ))}
        </tbody>
      </Table>

      <Button type='submit' variant='success'>
        Submit Schedule
      </Button>
    </>
  );
};

export default AddScheduleScreen;
