import React from "react";
import { Link } from "react-router-dom";
import { Card, CardBody } from "react-bootstrap";
import Rating from "./Rating";
import Message from "./Message";

const Teacher = ({ teacher, courseId }) => {
  if (!teacher) {
    return <div>Teacher information is missing</div>;
  }

  // Here we create the URL to navigate to the ScheduleScreen with the courseId and teacherId
  const scheduleLink = `/course/${courseId}/${teacher._id}/schedule`;

  return (
    <Card className="my-3 p-3 rounded">
      <Link to={scheduleUrl}>
        <Card.Img src={teacher.image} variant="top" />
      </Link>

      <Card.Body>
        <Link to={scheduleUrl}>
          <Card.Title as="div" style={{ textAlign: "center" }}>
            <strong>{teacher.name}</strong>
          </Card.Title>
        </Link>

        {/* Display teacher's rating using a Rating component */}
        <Rating value={teacher.rating} text={`${teacher.numReviews} reviews`} />

        <Card.Text as="h3">${teacher.price}</Card.Text>

        {/* Link to the teacher's schedule */}
        <Link to={scheduleUrl} className="btn btn-primary">
          View Schedule
        </Link>
      </Card.Body>
    </Card>
  );
};

export default Teacher;
