import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import { Table } from 'react-bootstrap';

const ScheduleScreen = () => {
    const [slots, setSlots] = useState([]);
    const [teacherName, setTeacherName] = useState('');
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    const { courseId, teacherId } = useParams();

    useEffect(() => {
        const fetchSchedule = async () => {
            const userInfo = JSON.parse(localStorage.getItem('userInfo'));
            if (userInfo) {
                const config = {
                    headers: {
                        Authorization: `Bearer ${userInfo.token}`,
                    },
                };
                try {
                    const { data } = await axios.get(`/api/schedule/${courseId}/${teacherId}`, config);
                    console.log("data" , data)
                    if (data && data.slots && data.slots.length > 0) {
                        setSlots(data.slots);
                        setTeacherName(data.teacherName); // assuming teacherName is part of the response
                    } else {
                        setError('No schedule available.');
                    }
                } catch (err) {
                    setError(err.response && err.response.data.message ? err.response.data.message : err.message);
                } finally {
                    setLoading(false);
                }
            }
        };
    
        fetchSchedule();
    }, [courseId, teacherId]);

    return (
        <div className="schedule-container">
            {loading ? (
                <p>Loading...</p>
            ) : error ? (
                <p>Error: {error}</p>
            ) : (
                <>
                    <h2>Schedule for {teacherName}</h2>
                    <Table striped bordered hover responsive>
                        <thead>
                            <tr>
                                <th>Date / Time</th>
                                {slots.length > 0 && slots[0].times.map((_, index) => (
                                    <th key={index}>Time Slot {index + 1}</th>
                                ))}
                            </tr>
                        </thead>
                        <tbody>
                            {slots.map((slot, index) => (
                                <tr key={index}>
                                    <td>{slot.date}</td>
                                    {slot.times.map((timeSlot, timeIndex) => (
                                        <td key={timeIndex}>{timeSlot.booked ? 'Booked' : 'Available'}</td>
                                    ))}
                                </tr>
                            ))}
                        </tbody>
                    </Table>
                </>
            )}
        </div>
    );
};

export default ScheduleScreen;
