import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import { Table } from 'react-bootstrap';

const ScheduleScreen = () => {
    // Changed from "slots" to "scheduleData" to match the later usage.
    const [scheduleData, setScheduleData] = useState({ dates: [], times: [], bookings: {} });
    const [teacherName, setTeacherName] = useState('');
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    const { courseId, teacherId } = useParams();

    useEffect(() => {
        const fetchSchedule = async () => {
            const userInfo = JSON.parse(localStorage.getItem('userInfo'));
            if (userInfo) {
                const config = {
                    headers: {
                        Authorization: `Bearer ${userInfo.token}`,
                    },
                };
                try {
                    const { data } = await axios.get(`/api/schedule/${courseId}/${teacherId}`, config);
                    if (data && data.slots) {
                        setScheduleData(data.slots);
                        setTeacherName(data.teacherName); // Assume that "teacherName" comes from the backend.
                    } else {
                        setError('No schedule available.');
                    }
                } catch (err) {
                    setError(err.response && err.response.data.message ? err.response.data.message : err.message);
                } finally {
                    setLoading(false);
                }
            }
        };
      
        fetchSchedule();
    }, [courseId, teacherId]);

    return (
        <div className="schedule-container">
            {loading ? (
                <p>Loading...</p>
            ) : error ? (
                <p>Error: {error}</p>
            ) : (
                <>
                    <h2>Schedule for {teacherName}</h2>
                    <Table striped bordered hover responsive>
                        <thead>
                            <tr>
                                <th>Date / Time</th>
                                {scheduleData.map((schedule, index) => (
                                    <th key={index}>{schedule.date}</th>
                                ))}
                            </tr>
                        </thead>
                        <tbody>
                            {scheduleData.map((schedule, index) => 
                                schedule.times.map((timeSlot, timeIndex) => (
                                    <tr key={`${index}-${timeIndex}`}>
                                        {timeIndex === 0 && <td rowSpan={schedule.times.length}>{schedule.date}</td>}
                                        <td>{timeSlot.time}</td>
                                        <td>{timeSlot.booked ? 'Booked' : 'Available'}</td>
                                    </tr>
                                ))
                            )}
                        </tbody>
                    </Table>
                </>
            )}
        </div>
    );
};

export default ScheduleScreen;
