import React from "react";
import { Link } from "react-router-dom";
import { Card, CardBody } from "react-bootstrap";
import Rating from "./Rating";
import teachers from "../teachers";
import Message from "./Message";

const Teacher = ({ teacher }) => {
  if (!teacher) {
    // This error handling ensures that we don't try to render the component if `teacher` is undefined
    console.error('Teacher prop is undefined');
    return <Message>Teacher information is missing</Message>;
  }

  return (
    <Card className="my-3 p-3 rounded">
      <Link to={`/teacher/${teacher._id}`}>
        <Card.Img src={teacher.image} variant="top" />
      </Link>

      <Card.Body>
        <Link to={`/teacher/${teacher._id}`}>
          <Card.Title as="div" style={{ textAlign: "center" }}>
            {teacher.name}
          </Card.Title>
        </Link>
        {/* Other teacher details */}
      </Card.Body>
    </Card>
  );
};

export default Teacher;
