import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import { Table } from 'react-bootstrap';

const ScheduleScreen = () => {
    const [scheduleData, setScheduleData] = useState({
        dates: [],
        times: [],
        bookings: {},
        teacherName: ''
    });
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');

    const { courseId, teacherId } = useParams();

    useEffect(() => {
        const fetchSchedule = async () => {
            const userInfo = JSON.parse(localStorage.getItem('userInfo'));
            if (userInfo) {
                const config = {
                    headers: {
                        Authorization: `Bearer ${userInfo.token}`,
                    },
                };
                try {
                    setLoading(true);
                    const response = await axios.get(`/api/schedule/${courseId}/${teacherId}`, config);
                    const scheduleList = response.data;

                    const dates = scheduleList.map(item => item.date);

                    // Collect all times for each date (assuming that each date has the same times)
                    const allTimes = new Set(scheduleList.flatMap(item => item.times.map(time => time.time)));

                    const times = Array.from(allTimes); // Convert the set to an array

                    // Construct the bookings object
                    const bookings = scheduleList.reduce((acc, item) => {
                        acc[item.date] = item.times
                            .filter(t => t.booked)
                            .map(t => t.time); // This creates an array of times where booked is true
                        return acc;
                    }, {});

                    setScheduleData({
                        dates,
                        times,
                        bookings,
                        teacherName: response.data.teacherName, // Adjust if necessary based on your API response
                    });

                    setLoading(false);

                } catch (err) {
                    console.error('Fetching Schedule Error:', err);
                    setError(err.response && err.response.data.message ? err.response.data.message : err.message);
                    setLoading(false);
                }
            }
        };

        fetchSchedule();
    }, [courseId, teacherId]);

    const handleSlotClick = async (date, time) => {
        // Initialize the bookings array for the date if it doesn't exist
        if (!Array.isArray(scheduleData.bookings[date])) {
            scheduleData.bookings[date] = [];
        }
    
        // Check if the time is already booked
        const isBooked = scheduleData.bookings[date].includes(time);
    
        // Copy the current bookings for immutability
        const updatedBookings = { ...scheduleData.bookings };
    
        if (isBooked) {
            // Remove the time from the bookings for that date
            updatedBookings[date] = updatedBookings[date].filter(t => t !== time);
        } else {
            // Add the time to the bookings for that date
            updatedBookings[date].push(time);
        }
    
        // Update the state with the new bookings
        setScheduleData(prevData => ({
            ...prevData,
            bookings: updatedBookings
        }));
    
        // Update the database
        try {
            const userInfo = JSON.parse(localStorage.getItem('userInfo'));
            if (userInfo) {
                const config = {
                    headers: {
                        Authorization: `Bearer ${userInfo.token}`,
                    },
                };
    
                // Construct the body of your request based on how your API expects it
                const requestBody = {
                    date,
                    time,
                    isBooked: !isBooked,
                };
                //console.log("requestBody", requestBody);
    
                await axios.put(`/api/schedule/update`, requestBody, config);
            }
        } catch (error) {
            console.error('Error updating the schedule:', error);
        }
    };
    
    

    
    return (
        <div className="schedule-container">
            {loading ? (
                <p>Loading...</p>
            ) : error ? (
                <p>Error: {error}</p>
            ) : (
                <>
                    <h2>Schedule for {scheduleData.teacherName}</h2>
                    <Table striped bordered hover responsive>
                        <thead>
                            <tr>
                                <th className="date-column">Date</th>
                                {scheduleData.dates?.map((date, index) => (
                                    <th key={index}>{date}</th>
                                ))}
                            </tr>
                        </thead>
                        <tbody>
                            {scheduleData.times?.map((time, index) => (
                                <tr key={index}>
                                    <td>{time}</td>
                                    {scheduleData.dates?.map((date, dateIndex) => {
                                        const isBooked = scheduleData.bookings[date]?.includes(time); // Check if booked
                                        const cellClass = isBooked ? 'booked' : 'available';
                                        return (
                                            <td
                                                key={`${date}-${time}`}
                                                className={cellClass}
                                                onClick={() => handleSlotClick(date, time)}
                                            >
                                                {isBooked ? 'Booked' : 'Available'}
                                            </td>
                                        );
                                    })}
                                </tr>
                            ))}
                        </tbody>
                    </Table>
                </>
            )}
        </div>
    );
};

export default ScheduleScreen;
