// In TeachersScreen.js
import React, { useEffect, useState } from "react";
import { Link, useLocation } from "react-router-dom";
import axios from 'axios';

const TeachersScreen = () => {
  const [teachers, setTeachers] = useState([]);
  const location = useLocation();
  const searchParams = new URLSearchParams(location.search);
  const course = searchParams.get('course'); // Assuming you pass the course as a query parameter

  useEffect(() => {
    const fetchTeachers = async () => {
      const { data } = await axios.get(`/api/teachers?course=${course}`);
      setTeachers(data);
    };

    fetchTeachers();
  }, [course]);

  return (
    <>
      <Link className="btn btn-dark my-3" to="/">
        Go Back
      </Link>
      <div>
        {teachers.map(() => (
          // Placeholder code to indicate that teacher variable is intentionally unused
          <div key={teachers.id}>{/* Display each teacher */}</div>
        ))}
      </div>
    </>
  );
};

export default TeachersScreen;
