import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link, useParams } from "react-router-dom";
import { Row, Col } from "react-bootstrap";
import Teacher from "../components/Teacher";
import Message from "../components/Message";
import Loader from "../components/Loader";
import { listTeachers } from "../actions/userActions";

const TeachersScreen = () => {
  const { id } = useParams();
  const dispatch = useDispatch();

  const teacherList = useSelector((state) => state.teacherList);
  const { loading, error, teachers } = teacherList;

  useEffect(() => {
    dispatch(listTeachers(id)); // Trigger the fetch teachers action
  }, [dispatch, id]);

  return (
    <>
      <Link to="/" className="btn btn-light">
        Go Back
      </Link>
      <h1>Our Teachers</h1>
      {loading ? (
        <Loader />
      ) : error ? (
        <Message variant="danger">{error}</Message>
      ) : (
        <Row>
          {teachers.map((teacher) => (
            <Col key={teacher._id} sm={12} md={6} lg={4} xl={3}>
              <Teacher teacher={teacher} /> // Use Teacher component to display each teacher
            </Col>
          ))}
        </Row>
      )}
    </>
  );
};

export default TeachersScreen;
