import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import { Table } from 'react-bootstrap';

const ScheduleScreen = () => {
    const [schedule, setSchedule] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    const { courseId, teacherId } = useParams();

    useEffect(() => {
        const fetchSchedule = async () => {
            const userInfo = JSON.parse(localStorage.getItem('userInfo'));
            if (userInfo) {
                const config = {
                    headers: {
                        Authorization: `Bearer ${userInfo.token}`,
                    },
                };
                try {
                    setLoading(true);
                    const { data } = await axios.get(`/api/schedule/${courseId}/${teacherId}`, config);
                    console.log(data); // Add this line to see what you are getting from the server
                    if (data && Array.isArray(data.slots) && data.slots.length > 0) {
                        setSchedule(data.slots);
                    } else {
                        // If data is not what is expected or slots are empty, handle it as an error or empty state.
                        setError('No schedule available.');
                    }
                } catch (err) {
                    setError(err.response && err.response.data.message ? err.response.data.message : err.message);
                } finally {
                    setLoading(false);
                }
            }
        };
      
        fetchSchedule();
    }, [courseId, teacherId]);
    

    // Function to handle class names based on booking status
    const getClassName = (booked) => {
        return booked ? 'table-danger' : 'table-success';
    };

    return (
        <div className="schedule-container">
            {loading ? (
                <p>Loading...</p>
            ) : error ? (
                <p>Error: {error}</p>
            ) : (
                <>
                    <h2>Schedule for Algebra</h2> {/* Assuming "Algebra" is the course name */}
                    <Table striped bordered hover responsive>
                        <thead>
                            <tr>
                                <th>Date</th>
                                <th>Time</th>
                                <th>Status</th>
                            </tr>
                        </thead>
                        <tbody>
                            {schedule.map(slot => (
                                slot.times.map((timeSlot, timeIndex) => (
                                    <tr key={`${slot.date}-${timeIndex}`} className={getClassName(timeSlot.booked)}>
                                        {timeIndex === 0 && <td rowSpan={slot.times.length}>{slot.date}</td>}
                                        <td>{timeSlot.time}</td>
                                        <td className={getClassName(timeSlot.booked)}>
                                            {timeSlot.booked ? 'Booked' : 'Available'}
                                        </td>
                                    </tr>
                                ))
                            ))}
                        </tbody>
                    </Table>
                </>
            )}
        </div>
    );
};

export default ScheduleScreen;
