import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import { Table } from 'react-bootstrap';

const ScheduleScreen = () => {
    const [scheduleData, setScheduleData] = useState([]);
    const [teacherName, setTeacherName] = useState('');
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');

    const { courseId, teacherId } = useParams();

    useEffect(() => {
        const fetchSchedule = async () => {
          const userInfo = JSON.parse(localStorage.getItem('userInfo'));
          if (userInfo) {
            const config = {
              headers: {
                Authorization: `Bearer ${userInfo.token}`,
              },
            };
            try {
              setLoading(true);
              const { data } = await axios.get(`/api/schedule/${courseId}/${teacherId}`, config);
              // Check if the data is an array and has at least one schedule
              if (Array.isArray(data) && data.length > 0) {
                setScheduleData(data);
              } else {
                // If data is empty or not in expected format, set an error message
                setError('No schedule available.');
              }
            } catch (err) {
              setError(err.response && err.response.data.message ? err.response.data.message : err.message);
            } finally {
              setLoading(false);
            }
          }
        };
      
        fetchSchedule();
      }, [courseId, teacherId]);
      

    return (
        <div className="schedule-container">
            {loading ? (
                <p>Loading...</p>
            ) : error ? (
                <p>Error: {error}</p>
            ) : (
                <>
                    <h2>Schedule for {teacherName}</h2>
                    <Table striped bordered hover responsive>
                    <thead>
                        <tr>
                            <th>Date / Time</th>
                            {scheduleData.dates.map((date, index) => (
                            <th key={index}>{date}</th>
                            ))}
                        </tr>
                    </thead>
                    <tbody>
                        {scheduleData.times.map((time, index) => (
                            <tr key={index}>
                            <td>{time}</td>
                            {scheduleData.dates.map((date, dateIndex) => (
                                <td key={`${date}-${time}`}>
                                {scheduleData.bookings[date]?.includes(time) ? 'Booked' : 'Available'}
                                </td>
                            ))}
                            </tr>
                        ))}
                    </tbody>
                </Table>
                </>
            )}
        </div>
    );
};

export default ScheduleScreen;
