import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link, useParams } from "react-router-dom";
import { Row, Col, Image, ListGroup } from "react-bootstrap";
import Rating from "../components/Rating";
import teachers from "../teachers";
import Teacher from "../components/Teacher";
import Message from "../components/Message";
import Loader from "../components/Loader";
import { listTeachers } from "../actions/userActions";
import Meta from "../components/Meta.js";
import CourseCarousel from "../components/CourseCarousel.js";

const TeachersScreen = () => {

  const { id } = useParams();

  const dispatch = useDispatch();

  const teacherList = useSelector((state) => state.teacherList);
  const { loading, error, teachers } = teacherList;

  //whatever we put inisde the useEffect func will run when the HomeScreen component loads
  //what we want is that our coreses loads to the screen
  useEffect(() => {
    dispatch(listTeachers(id));
  }, [dispatch]); // Add keyword to dependency array



  return (
    <>
      <Meta />
      {!id ? <CourseCarousel/> : <Link to='/' className='btn btn-light'>Go Back </Link> }
      <h1>Our Teachers</h1>
      {loading ? (
        <Loader />
      ) : error ? (
        <Message variant='danger'>{error}</Message>
      ) : (
        <Row>
          {teachers.map((teacher) => (
            <Col key={teacher._id} sm={12} md={6} lg={4} xl={3}>
              <Teacher teacher={teacher} />
            </Col>
          ))}
        </Row>
      )}
    </>
  );

};

export default TeachersScreen;
