import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import '../design/ScheduleScreen.css';

const ScheduleScreen = () => {
    const [scheduleData, setScheduleData] = useState({ dates: [], times: [], bookings: {} });
    const { courseId, teacherId } = useParams();

    useEffect(() => {
        const fetchSchedule = async () => {
          try {
            const token = localStorage.getItem("userInfo") ? JSON.parse(localStorage.getItem("userInfo")).token : null;
            const config = {
              headers: {
                Authorization: `Bearer ${token}`,
              },
            };
            const response = await axios.get(`/api/schedule/${courseId}/${teacherId}`, config);
            setScheduleData(response.data);
          } catch (error) {
            console.error('There was an error fetching the schedule data:', error);
          }
        };
      
        if (courseId && teacherId) {
          fetchSchedule();
        }
      }, [courseId, teacherId]);

      const bookTimeSlot = (date, time) => {
        const token = localStorage.getItem("userInfo") ? JSON.parse(localStorage.getItem("userInfo")).token : null;
        const config = {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        };
      
        axios.post(`/api/schedule/book`, { courseId, teacherId, date, time }, config)
          .then(() => {
            // your existing logic
          })
          .catch(error => {
            console.error('There was an error booking the time slot:', error);
          });
      };

    if (Object.keys(scheduleData).length === 0) {
        return null; // or render a loading spinner or an error message
    }

    return (
        <div className="schedule-container">
            <h2>Schedule</h2>
            <table>
                <thead>
                    <tr>
                        {scheduleData.dates.map(date => <th key={date}>{date}</th>)}
                    </tr>
                </thead>
                <tbody>
                    {scheduleData.times.map(time => (
                        <tr key={time}>
                            {scheduleData.dates.map(date => (
                                <td
                                    key={`${date}-${time}`}
                                    className={scheduleData.bookings[date]?.includes(time) ? 'booked' : 'available'}
                                    onClick={() => !scheduleData.bookings[date]?.includes(time) && bookTimeSlot(date, time)}
                                >
                                    {time}
                                </td>
                            ))}
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
}

export default ScheduleScreen;
