import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import { Table } from 'react-bootstrap';

const ScheduleScreen = () => {
    const [schedule, setSchedule] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const [teacherName , setTeacherName] = useState('');

    const { courseId, teacherId } = useParams();

    useEffect(() => {
        const fetchSchedule = async () => {
            const userInfo = JSON.parse(localStorage.getItem('userInfo'));
            if (userInfo) {
                const config = {
                    headers: {
                        Authorization: `Bearer ${userInfo.token}`,
                    },
                };
                try {
                    const { data } = await axios.get(`/api/schedule/${courseId}/${teacherId}`, config);
                    if (data && data.slots) {
                        setSchedule(data.slots);
                    } else {
                        setError('No schedule available.');
                    }
                } catch (err) {
                    setError(err.response && err.response.data.message ? err.response.data.message : err.message);
                } finally {
                    setLoading(false);
                }
            }
        };
      
        fetchSchedule();
    }, [courseId, teacherId]);

    return (
        <div className="schedule-container">
            {loading ? (
                <p>Loading...</p>
            ) : error ? (
                <p>Error: {error}</p>
            ) : (
                <>
                    <h2>Schedule for {teacherName}</h2>
                    <Table striped bordered hover responsive>
                        <thead>
                            <tr>
                                <th>Date / Time</th>
                                {schedule.map(slot => slot.times.map((_, timeIndex) => (
                                    <th key={timeIndex}>{slot.date}</th>
                                )))}
                            </tr>
                        </thead>
                        <tbody>
                            {schedule.map(slot => (
                                slot.times.map((timeSlot, timeIndex) => (
                                    <tr key={`${slot.date}-${timeIndex}`} className={timeSlot.booked ? 'bg-danger' : 'bg-success'}>
                                        <td>{timeSlot.time}</td>
                                        <td>{timeSlot.booked ? 'Booked' : 'Available'}</td>
                                    </tr>
                                ))
                            ))}
                        </tbody>
                    </Table>
                </>
            )}
        </div>
    );
};

export default ScheduleScreen;
